import { Dialog } from "@mui/material";
import React, { Component, useState } from "react";
import { Link } from "react-router-dom";
import { RadarChart } from "../../component/radar/RadarChart";

const PortfolioListContent = [
  {
    image: "image-1",
    category: "Development",
    title: "Getting tickets to the big show",
  },
  {
    image: "image-2",
    category: "Development",
    title: "Getting tickets to the big show",
  },
  {
    image: "image-3",
    category: "Development",
    title: "Getting tickets to the big show",
  },
  {
    image: "image-4",
    category: "Development",
    title: "Getting tickets to the big show",
  },
  {
    image: "image-3",
    category: "Development",
    title: "Getting tickets to the big show",
  },
  {
    image: "image-4",
    category: "Development",
    title: "Getting tickets to the big show",
  },
];

export default function PortfolioList(props) {
  const { column, styevariation,  porfolioProjects, item = None } = props;
  const [dialogOpen, setDialogOpen] = useState(false);
  const list = porfolioProjects.slice(0, item);
  function onClickDialog() {
    setDialogOpen(true);
  }

  function closeDialog() {
    setDialogOpen(false);
  }
  return (
    <>
      {list.map((value, index) => (
        <>
          <div className={`${column}`} key={index}>
            <div className={`portfolio ${styevariation}`}>
              <div className="thumbnail-inner">
                <div className={`thumbnail ${value.image}`}></div>
                <div className={`bg-blr-image ${value.image}`}></div>
              </div>
              <div className="content">
                <div className="inner">
                  <p>{value.category}</p>
                  <h4>
                    <a href="/portfolio-details">{value.title}</a>
                  </h4>
                  <div className="portfolio-button">
                    <a className="rn-btn" onClick={onClickDialog}>
                      View Details
                    </a>
                  </div>
                </div>
              </div>
              <Link className="link-overlay" to="/portfolio-details"></Link>
            </div>
          </div>
          <Dialog fullScreen open={dialogOpen} onClose={closeDialog}>
            <RadarChart></RadarChart>
          </Dialog>
        </>
      ))}
    </>
  );
}
