import { Card, Dialog, Grid, Paper, CardContent } from "@mui/material";
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import Slide from "@mui/material/Slide";
import Box from "@mui/material/Box";
import { Button } from "@mui/material";
import { KeyboardArrowLeft, KeyboardArrowRight } from "@mui/icons-material";
import IconButton from "@mui/material/IconButton";
import { MobileStepper } from "@mui/material";
import Rating from "@mui/material/Rating";
import { Stack } from "@mui/material";
import { CardHeader } from "@mui/material";
import Typography from "@mui/material/Typography";
import CloseIcon from "@mui/icons-material/Close";
import Drawer from "@mui/material/Drawer";
import { StarBorder, Star } from "@mui/icons-material";
import { TransitionProps } from "@mui/material/transitions";
import { RadarChart } from "../../component/radar/RadarChart";
import { makeStyles } from "@mui/styles";
import { height } from "@mui/system";

const useStyles = makeStyles({
  black: { background: "black", color: "black" },
});

export default function PortfolioDialog(props) {
  const {
    initUserRating,
    dialogOpen,
    setDialogOpen,
    totalPorfolio,
    porfolioProjects,
    porfolioProject,
    userData,
    setSnackbarMessage,
    setSnackbarOpen,
  } = props;
  const [userRating, setUserRating] = useState(initUserRating);
  const [activeStep, setActiveStep] = useState();
  const [buttonDisable, setButtonDisable] = useState(false);
  const [selectedPorfolioProject, setSelectedPorfolioProject] =
    useState(porfolioProject);
  useEffect(() => {
    if (!Object.keys(initUserRating).length) {
      const newUserRating = {};
      porfolioProject.rating_mean_score.map(
        (item) => (newUserRating[item.rating_name] = 0)
      );
      setUserRating(newUserRating);
      console.log(newUserRating);
    } else {
      setUserRating(initUserRating);
    }
    const porfolioIndex = porfolioProjects.findIndex(
      (item) => item.id === porfolioProject.id
    );
    setActiveStep(porfolioIndex);
  }, [props.initUserRating]);

  function closeDialog() {
    setDialogOpen(false);
  }
  function setRatingValue(newValue, rating_name) {
    setUserRating((prevState) => {
      return { ...prevState, [rating_name]: newValue };
    });
  }

  function onClickRatingSubmit() {
    setButtonDisable(true);
    fetch("http://localhost:5000/postPortfolioRating", {
      method: "POST",
      body: JSON.stringify({
        value: JSON.stringify({
          portfolio_user_id: userData.id,
          portfolio_project_id: selectedPorfolioProject.id,
          testimonial_rating: testRating,
        }),
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        setSnackbarMessage("Testimonial Submitted, Thanks for the review ");
        setSnackbarOpen(true);
        setButtonDisable(false);
      });
  }

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSelectedPorfolioProject(porfolioProjects[activeStep + 1]);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
    setSelectedPorfolioProject(porfolioProjects[activeStep - 1]);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  //   const Transition = React.forwardRef(function Transition(
  //     props: TransitionProps & {
  //       children: React.ReactElement<any, any>,
  //     },
  //     ref: React.Ref<unknown>
  //   ) {
  //     return <Slide direction="right" ref={ref} {...props} />;
  //   });

  return (
    <Dialog
      fullScreen
      open={dialogOpen}
      onClose={closeDialog}

      //   TransitionComponent={Transition}
    >
      <Box
        sx={{
          backgroundColor: "black",
          height: "100%",
        }}
      >
        {/* <Paper elevation={5}> */}
        <Box
          sx={{
            backgroundColor: "#9fa8da",
            mx: { lg: "15rem", xs: 0 },
            height: "100%",
            justifyContent: "center",
          }}
          elevation={5}
        >
          <Grid container alignItems="stretch">
            <Grid item xs={12} lg={6}>
              <Card
                elevation={5}
                sx={{
                  backgroundColor: "#5c6bc0",
                  height: "80%",
                  p: 3,
                  m: 3,
                }}
              >
                <CardHeader
                  title={`${porfolioProject.project_name}`}
                  subheader="Video illustration of sample app"
                  sx={{ backgroundColor: "#d1c4e9" }}
                />
                <CardContent sx={{ width: 1, height: 1 }}>
                  {/* <motion.div layout> */}
                  <iframe
                    src={porfolioProject.video_link}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    title="Embedded youtube"
                    height="100%"
                    width="100%"
                  />
                  {/* </motion.div> */}
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} lg={6}>
              <Card sx={{ m: 2, backgroundColor: "#bbdefb" }} elevation={5}>
                <RadarChart
                  radarDatafield={selectedPorfolioProject.rating_mean_score}
                ></RadarChart>
              </Card>
              <Card sx={{ m: 2, backgroundColor: "#b2ebf2" }} elevation={5}>
                <CardHeader
                  title="Your Rating"
                  subheader="Please rate to improve the project"
                  sx={{ backgroundColor: "#80cbc4" }}
                />
                {userRating && (
                  <Box p={3}>
                    {selectedPorfolioProject.rating_mean_score.map((item) => (
                      <>
                        <Typography component="legend">{`${item.rating_name}`}</Typography>
                        <Rating
                          name={item.rating_name}
                          value={userRating[item.rating_name]}
                          onChange={(event, newValue) => {
                            setRatingValue(newValue, item.rating_name);
                          }}
                          emptyIcon={<StarBorder sx={{ fontSize: 35 }} />}
                          icon={
                            <Star sx={{ fontSize: 35, color: "#01579b" }} />
                          }
                        />
                      </>
                    ))}
                  </Box>
                )}
                <Grid p={3} item xs={12}>
                  <Button
                    variant="contained"
                    color="success"
                    size="large"
                    disabled={buttonDisable}
                    onClick={onClickRatingSubmit}
                  >
                    Confirm
                  </Button>
                </Grid>
              </Card>
            </Grid>
          </Grid>
        </Box>
        <MobileStepper
          steps={totalPorfolio}
          position="bottom"
          sx={{ backgroundColor: "#424242" }}
          activeStep={activeStep}
          nextButton={
            <Button
              size="small"
              onClick={handleNext}
              sx={{ color: "#fafafa", fontSize: 25 }}
              disabled={activeStep === totalPorfolio - 1}
            >
              Next Project
              <KeyboardArrowRight />
            </Button>
          }
          backButton={
            <Button
              size="small"
              onClick={handleBack}
              sx={{ color: "#fafafa", fontSize: 25 }}
              disabled={activeStep === 0}
            >
              <KeyboardArrowLeft />
              Previous Project
            </Button>
          }
        />
      </Box>
    </Dialog>
  );
}
