import { Card, Dialog, Grid } from "@mui/material";
import React, { Component, useEffect, useState } from "react";
import { useTheme } from "@mui/styles";
import Slide from "@mui/material/Slide";
import Box from "@mui/material/Box";
import { Button } from "@mui/material";
import { KeyboardArrowLeft, KeyboardArrowRight } from "@mui/icons-material";
import IconButton from "@mui/material/IconButton";
import { MobileStepper } from "@mui/material";
import Rating from "@mui/material/Rating";
import { Stack } from "@mui/material";
import Typography from "@mui/material/Typography";
import CloseIcon from "@mui/icons-material/Close";
import Drawer from "@mui/material/Drawer";
import { TransitionProps } from "@mui/material/transitions";
import { RadarChart } from "../../component/radar/RadarChart";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles({
  black: { background: "black", color: "black" },
});

export default function PortfolioDialog(props) {
  const {
    initUserRating,
    dialogOpen,
    setDialogOpen,
    totalPorfolio,
    porfolioProjects,
    porfolioProject = null,
  } = props;
  const [userRating, setUserRating] = useState(initUserRating);
  const [activeStep, setActiveStep] = useState();
  const theme = useTheme();
  const classes = useStyles();
  useEffect(() => {
    if (!initUserRating) {
      const newUserRating = {};
      porfolioProject.rating_mean_score.map(
        (item) => (newUserRating[item.rating_name] = 0)
      );
      setUserRating(newUserRating);
    } else {
      setUserRating(initUserRating);
    }
    
  }, [props.initUserRating]);

  function closeDialog() {
    setDialogOpen(false);
  }
  function setRatingValue(newValue, rating_name) {
    setUserRating((prevState) => {
      return { ...prevState, [rating_name]: newValue };
    });
  }

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  //   const Transition = React.forwardRef(function Transition(
  //     props: TransitionProps & {
  //       children: React.ReactElement<any, any>,
  //     },
  //     ref: React.Ref<unknown>
  //   ) {
  //     return <Slide direction="right" ref={ref} {...props} />;
  //   });

  return (
    <Dialog
      fullScreen
      open={dialogOpen}
      onClose={closeDialog}

      //   TransitionComponent={Transition}
    >
      <Box
        sx={{
          backgroundColor: "black",
          height: "100%",
        }}
      >
        <Box
          sx={{
            backgroundColor: "lightBlue",
            mx: "20rem",
            height: "100%",
            justifyContent: "center",
            elevation: 5,
          }}
        >
          <Grid container>
            <Grid item xs={6}>
              <iframe
                width="853"
                height="480"
                src={`https://www.youtube.com/embed/BpNm7GRkq38`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="Embedded youtube"
              />
            </Grid>
            <Grid item xs={6}>
              <RadarChart
                radarDatafield={porfolioProject.rating_mean_score}
              ></RadarChart>
              {userRating && (
                <Box p={3}>
                  {porfolioProject.rating_mean_score.map((item) => (
                    <>
                      <Typography component="legend">{`${item.rating_name}`}</Typography>
                      <Rating
                        name={item.rating_name}
                        value={userRating[item.rating_name]}
                        onChange={(event, newValue) => {
                          setRatingValue(newValue, item.rating_name);
                        }}
                      />
                    </>
                  ))}
                </Box>
              )}
            </Grid>
          </Grid>
        </Box>
        <MobileStepper
          steps={totalPorfolio}
          position="static"
          activeStep={activeStep}
          nextButton={
            <Button
              size="small"
              onClick={handleNext}
              disabled={activeStep === totalPorfolio - 1}
            >
              Next Project
              <KeyboardArrowRight />
            </Button>
          }
          backButton={
            <Button
              size="small"
              onClick={handleBack}
              disabled={activeStep === 0}
            >
              <KeyboardArrowLeft />
              Previous Project
            </Button>
          }
        />
      </Box>
    </Dialog>
  );
}
