import React, { Component, useState } from "react";
import { Link } from "react-router-dom";
import PortfolioDialog from "./PortfolioDialog";

export default function PortfolioList(props) {
  const {
    column,
    styevariation,
    porfolioProjects,
    item = null,
    userData = null,
  } = props;
  const [dialogOpen, setDialogOpen] = useState(false);
  const [porfolioProject, setPorfolioProject] = useState();
  const [initUserRating, setInitUserRating] = useState();
  const list =
    item != null ? porfolioProjects.slice(0, item) : porfolioProjects;

  function onClickDialog(value) {
    const filter_project = userData.rating_records.filter(
      (item) => item.project_name === value.project_name
    );
    let mapUserRating = {}
    f
        //   .map((item) => {
        //     return { ...filter_project, [item.rating_name]: item.rating_score };
        //   });
    console.log(filter_project, value.project_name);
    setInitUserRating(filter_project);
    setDialogOpen(true);
    setPorfolioProject(value);
  }

  return (
    <>
      {list.map((value, index) => (
        <>
          <div className={`${column}`} key={index}>
            <div className={`portfolio ${styevariation}`}>
              <div className="thumbnail-inner">
                <div className={`thumbnail ${value.image}`}></div>
                <div className={`bg-blr-image ${value.image}`}></div>
              </div>
              <div className="content">
                <div className="inner">
                  <p>{value.category}</p>
                  <h4>
                    <a href="/portfolio-details">{value.project_name}</a>
                  </h4>
                  <div className="portfolio-button">
                    <a
                      className="rn-btn"
                      onClick={(event) => onClickDialog(value)}
                    >
                      View Details
                    </a>
                  </div>
                </div>
              </div>
              <Link className="link-overlay" to="/portfolio-details"></Link>
            </div>
          </div>
        </>
      ))}
      {porfolioProject && (
        <PortfolioDialog
          key={porfolioProject.id}
          porfolioProject={porfolioProject}
          dialogOpen={dialogOpen}
          setDialogOpen={setDialogOpen}
          initUserRating={initUserRating}
        />
      )}
    </>
  );
}
