import React from "react";
import { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import ScrollToTop from "react-scroll-up";
import { FiChevronUp } from "react-icons/fi";
import { motion } from "framer-motion";
import { Button, Card, Rating, Grid } from "@mui/material";
import { Star } from "@mui/icons-material";
import Snackbar from "@mui/material/Snackbar";
import Alert from "@mui/material/Alert";
import Helmet from "../component/common/Helmet";
import TextLoop from "react-text-loop";
import HeaderThree from "../component/header/HeaderThree";
import FooterTwo from "../component/footer/FooterTwo";
import TabTwo from "../elements/tab/TabTwo";
import ContactThree from "../elements/contact/ContactThree";
import PortfolioList from "../elements/portfolio/PortfolioList";
import ServiceList from "../elements/service/ServiceList";
import BlogContent from "../elements/blog/BlogContent";

const SlideList = [
  {
    textPosition: "text-left",
    category: "Welcome to my World",
    // description: "abcdefg",
    // buttonText: "testing",
    // buttonLink: "",
  },
];
const PortfolioLanding = () => {
  const [userData, setUserData] = useState({});
  const [porfolioProjects, setPorfolioProjects] = useState([]);
  const [welcomeMessage, setWelcomeMessage] = useState("");
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [testimonialList, setTestimonialList] = useState([]);

  let { loginType, tokenId } = useParams();

  const history = useHistory();
  useEffect(() => {
    fetch("http://localhost:5000/getUserProfile", {
      method: "POST",
      body: JSON.stringify({
        value: JSON.stringify({ loginType: loginType, tokenId: tokenId }),
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        data === "error" ? history.push("/login") : setUserData(data);
      })
      .catch((error) => {
        console.log(error);
        history.push("/login");
      });
    fetch("http://localhost:5000/getPorfolioProjects")
      .then((res) => res.json())
      .then((data) => {
        setPorfolioProjects(data);
      });
  }, []);

  useEffect(() => {
    Object.keys(userData).length &&
      setWelcomeMessage(`Hi, ${userData.displayname}`);
    console.log(`Hi, ${userData.displayname}`);
  }, [userData]);

  useEffect(() => {
    fetch("http://localhost:5000/getTestimonial")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setTestimonialList(data);
      });
  }, [snackbarOpen]);

  function handleClose() {
    setSnackbarOpen(false);
  }

  const welcomeVariant = {
    hidden: {
      //   x: "-100vw",
      opacity: 1,
    },
    visible: {
      //   x: 0,
      opacity: 1,
      transition: {
        // duration: 2,
        delay: 0.5,
        when: "beforeChildren",
        staggerChildren: 0.1,
      },
    },
  };

  const welcomeUserVariant = {
    hidden: {
      //   x: "-100vw",
      opacity: 1,
    },
    visible: {
      //   x: 0,
      opacity: 1,
      transition: {
        // duration: 2,
        delay: 0.5,
        staggerChildren: 0.1,
      },
    },
  };

  const welcomeLetterVariant = {
    hidden: {
      //   x: "-100vw",
      opacity: 0,
      y: 50,
    },
    visible: {
      //   x: 0,

      opacity: 1,
      y: 0,
    },
  };

  const welcomePorfolioVariant = {
    hidden: {
      //   x: "-100vw",
      opacity: 0,
      //   y: 50,
    },
    visible: {
      //   x: 0,
      delay: 1,
      opacity: 1,
      transition: { duration: 2 },
    },
  };

  let title = "About Me",
    description =
      "A motivated individual who masters and develops programming skills through self-teaching and build system which benefit the company and field. Consistently recognized as a competent leader who bring motivation and cohesiveness to his team.";
  const PostList = BlogContent.slice(0, 3);
  return Object.keys(userData).length ? (
    <div className="active-dark">
      <Helmet pageTitle="Portfolio Landing" />

      <HeaderThree homeLink="/" logo="symbol-dark" color="color-black" />
      {/* Start Slider Area   */}
      <div id="home" className="fix">
        <div className="slider-wrapper">
          {/* Start Single Slide */}
          {SlideList.map((value, index) => (
            <div
              className="slide personal-portfolio-slider slider-paralax slider-style-3 d-flex align-items-center justify-content-center bg_image bg_image--25"
              key={index}
            >
              <div className="container">
                <div className="row">
                  <div className="col-lg-12">
                    <motion.div
                      variants={welcomeVariant}
                      initial="hidden"
                      animate="visible"
                      className={`inner ${value.textPosition}`}
                    >
                      {value.category ? <span>{value.category}</span> : ""}
                      <div>
                        {welcomeMessage && (
                          <motion.h1
                            className="title"
                            variants={welcomeUserVariant}
                          >
                            {welcomeMessage.split("").map((char, index) => {
                              return (
                                <motion.span
                                  key={`${char}-${index}`}
                                  variants={welcomeLetterVariant}
                                >
                                  {char}
                                </motion.span>
                              );
                            })}
                          </motion.h1>
                        )}
                      </div>
                      <br />
                      <motion.h1
                        variants={welcomePorfolioVariant}
                        className="title"
                      >
                        Welcome to Clarence Portfolio <br />
                        <TextLoop>
                          <span> A Full Stack Developer.</span>
                          <span> Specialize in Python.</span>
                          <span> A React.js Designer.</span>
                          {/* <span> Content Writter.</span> */}
                        </TextLoop>{" "}
                      </motion.h1>
                      <motion.h2 variants={welcomePorfolioVariant}>
                        based in Singapore.
                      </motion.h2>
                      {value.description ? (
                        <p className="description">{value.description}</p>
                      ) : (
                        ""
                      )}
                      {value.buttonText ? (
                        <div className="slide-btn">
                          <a
                            className="rn-button-style--2 btn-primary-color"
                            href={`${value.buttonLink}`}
                          >
                            {value.buttonText}
                          </a>
                        </div>
                      ) : (
                        ""
                      )}
                    </motion.div>
                  </div>
                </div>
              </div>
            </div>
          ))}
          {/* End Single Slide */}
        </div>
      </div>
      {/* End Slider Area   */}

      {/* Start About Area */}
      <div id="about" className="fix">
        <div className="about-area ptb--120  bg_color--1">
          <div className="about-wrapper">
            <div className="container">
              <div className="row row--35 align-items-center">
                <div className="col-lg-5">
                  <div className="thumbnail">
                    <img
                      className="w-100"
                      src="/assets/images/bg/bg-image-34.gif"
                      alt="About Images"
                    />
                  </div>
                </div>
                <div className="col-lg-7">
                  <div className="about-inner inner">
                    <div className="section-title">
                      <h2 className="title">{title}</h2>
                      <p className="description">{description}</p>
                    </div>
                    <div className="row mt--30">
                      <TabTwo tabStyle="tab-style--1" />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* End About Area */}

      {/* Start Service Area  */}
      <div id="service" className="fix">
        <div className="service-area creative-service-wrapper ptb--120 bg_color--5">
          <div className="container">
            <div className="row">
              <div className="col-lg-12">
                <div className="section-title text-center service-style--3 mb--30 mb_sm--0">
                  <h2 className="title">My Awesome Service</h2>
                  <p>
                    There are many variations of passages of Lorem Ipsum
                    available, but the majority have suffered alteration.
                  </p>
                </div>
              </div>
            </div>
            <div className="row creative-service">
              <div className="col-lg-12">
                <ServiceList
                  item="6"
                  column="col-lg-4 col-md-6 col-sm-6 col-12 text-left"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* End Service Area  */}

      {/* Start Portfolio Area */}
      <div id="portfolio" className="fix">
        <div className="portfolio-area ptb--120 bg_color--1">
          <div className="portfolio-sacousel-inner">
            <div className="container">
              <div className="row">
                <div className="col-lg-12">
                  <div className="section-title text-center service-style--3 mb--30 mb_sm--0">
                    <h2 className="title">My Latest Project</h2>
                    <p>
                      There are many variations of passages of Lorem Ipsum
                      available, but the majority have suffered alteration.
                    </p>
                  </div>
                </div>
              </div>
              <div className="row">
                <PortfolioList
                  styevariation="text-center mt--40"
                  column="col-lg-4 col-md-6 col-sm-6 col-12"
                  porfolioProjects={porfolioProjects}
                  userData={userData}
                  setUserData={setUserData}
                />
              </div>
              <div className="row">
                <div className="col-lg-12">
                  <div className="view-more-btn mt--60 mt_sm--30 text-center">
                    <a className="rn-button-style--2 btn-solid" href="/blog">
                      <span>View More</span>
                    </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* End Portfolio Area */}

      {/* Start Blog Area */}
      <div id="testimonial" className="fix">
        <div className="rn-blog-area ptb--120 bg_color--5 mb-dec--30">
          <div className="container">
            <div className="row align-items-end">
              <div className="col-lg-12 col-md-12 col-sm-12 col-12">
                <div className="section-title text-center">
                  <h2>Testimonial</h2>
                  <p>
                    Below are Testimonial and Review from vistors of this site
                    <br />
                    Feel free to contribute your review with using the feature
                    below
                  </p>
                </div>
              </div>
            </div>
            <div className="row mt--60 mt_sm--40">
              <div className="row creative-service">
                <Grid container>
                  {testimonialList.map((value, i) => (
                    <Grid item xs={12} md={4} sm={6}>
                      <Card
                        sx={{
                          backgroundColor: "#434343",
                          width: "350px",
                          height: "400px",
                          color: "#fafafa",
                          bx: 5,
                        }}
                      >
                        <Rating
                          value={value.testimonial_rating}
                          readOnly={true}
                          icon={
                            <Star sx={{ fontSize: 50, color: "#ab003c" }} />
                          }
                        />
                        <p>{value.testimonial_text}</p>
                        <p>{value.testimonial_date}</p>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* End Blog Area */}

      {/* Start COntact Area */}
      <div id="contact" className="fix">
        <div className="rn-contact-area ptb--120 bg_color--1">
          <ContactThree
            contactImages="/assets/images/about/about-9.gif"
            contactTitle="Hire/ Connect Me."
            userData={userData}
            setSnackbarMessage={setSnackbarMessage}
            setSnackbarOpen={setSnackbarOpen}
          />
        </div>
      </div>
      {/* End COntact Area */}

      <FooterTwo />
      {/* Start Back To Top */}
      <div className="backto-top">
        <ScrollToTop showUnder={160}>
          <FiChevronUp />
        </ScrollToTop>
      </div>
      {/* End Back To Top */}
      <Snackbar
        open={snackbarOpen}
        onClose={handleClose}
        autoHideDuration={3000}
      >
        <Alert
          variant="filled"
          onClose={handleClose}
          severity="success"
          sx={{ width: "100%" }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </div>
  ) : null;
};

export default PortfolioLanding;
