import React, { createRef } from "react";
import { RadarOptions } from "./RadarConfig";
import { Radar } from "react-chartjs-2";

export function RadarChart(props) {
  const { radarDatafield } = props;

  const charRef = createRef();
  const RadarData = {
    labels: radarDatafield.map((item) => item.rating_name),
    // labels: [
    //   "Creative",
    //   "Usefulness",
    //   "Technology ",
    //   "User Interface",
    //   "Security",
    // ],
    datasets: [
      {
        label: "March",
        backgroundColor: "rgba(34, 202, 236, .2)",
        borderColor: "rgba(34, 202, 236, 1)",
        pointBackgroundColor: "rgba(34, 202, 236, 1)",
        poingBorderColor: "#fff",
        pointHoverBackgroundColor: "#fff",
        pointHoverBorderColor: "rgba(34, 202, 236, 1)",
        data: radarDatafield.map((item) => item.average_score),
        // data: [13, 10, 12, 6, 5],
      },
    ],
  };
  console.log(radarDatafield, RadarData);
  return <Radar ref={charRef} data={RadarData} options={RadarOptions} />;
}
