import React, { Component } from "react";
import { Typography } from "@mui/material";
import { FiCast, FiLayers, FiUsers, FiMonitor } from "react-icons/fi";
import { VscGear } from "react-icons/vsc";
import { motion } from "framer-motion";

const svgVariants={}


const ServiceList = [
  {
    icon: <FiLayers />,
    title: "Full Stack Software Engineer",
    description: [
      "Developing front end website architecture.",
      "Designing user interactions on web pages.",
      "Developing back-end website applications.",
      "Creating servers and databases for functionality.",
      // "Ensuring cross-platform optimization for mobile phones.",
      // "Ensuring responsiveness of applications.",
      // "Working alongside graphic designers for web design features.",
      // "Seeing through a project from conception to finished product.",
      // "Designing and developing APIs.",
      // "Meeting both technical and consumer needs.",
      // "Staying abreast of developments in web applications and programming languages.",
    ],
  },
  {
    icon: <FiLayers />,
    title: "Automation Software Engneer",
    description: [
      "Exploring and implementing new ways to automate systems",
      "Designing and testing automation equipment and processes",
      "Programming new automated components",
      "Identifying quality issues and writing reports,",
    ],
    svg: (
      <motion.svg
        xmlns="http://www.w3.org/2000/svg"
        className="h-6 w-6"
        fill="none"
        viewBox="0 0 24 24"
        stroke="currentColor"
        strokeWidth={2}
        variants={svgVariants}
        whileHover="hover"
        
      >
        <motion.path
          strokeLinecap="round"
          strokeLinejoin="round"
          d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.368 2.684 3 3 0 00-5.368-2.684z"
          variants={pathVariants}
        />
      </motion.svg>
    ),
  },
  {
    icon: <FiUsers />,
    title: " ",
    description: [
      "I throw myself down among the tall grass by the stream as I lie close to the earth.",
    ],
  },
  {
    icon: <FiMonitor />,
    title: "Mobile App Development",
    description: [
      "I throw myself down among the tall grass by the stream as I lie close to the earth.",
    ],
  },
  {
    icon: <FiUsers />,
    title: "Marketing & Reporting",
    description: [
      "I throw myself down among the tall grass by the stream as I lie close to the earth.",
    ],
  },
  {
    icon: <FiMonitor />,
    title: "Mobile App Development",
    description: [
      "I throw myself down among the tall grass by the stream as I lie close to the earth.",
    ],
  },
];

export default function ServiceThree(props) {
  const { column, item } = props;
  const ServiceContent = ServiceList.slice(0, item);

  return (
    <div className="row">
      {ServiceContent.map((val, i) => (
        <div className={`${column}`} key={i}>
          <div className="service service__style--2">
            <div className="icon">{val.icon}</div>
            <div className="content">
              <h3 className="title">{val.title}</h3>
              {val.description.map((pointer) => (
                <p>
                  <VscGear />

                  {`  ${pointer}`}
                </p>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
